def min_difference(a, b, c):
    total_duration = a + b + c
    half_duration = total_duration // 2

    # Generate all possible duration combinations
    durations = []
    for i in range(a + 1):
        for j in range(b + 1):
            for k in range(c + 1):
                duration = i + 2*j + 3*k
                durations.append(duration)

    # Find the difference between the closest combination to half_duration and half_duration
    min_diff = float('inf')
    for duration in durations:
        diff = abs(duration - half_duration)
        if diff < min_diff:
            min_diff = diff

    return min_diff
