def min_concert_duration_difference(a, b, c):
    total_duration = a + b*2 + c*3
    half_duration = total_duration / 2

    # Calculate the minimum possible difference in the durations of the concerts
    dp = [False] * (half_duration + 1)
    dp[0] = True

    for song in [a, b, c]:
        for i in range(half_duration, song - 1, -1):
            dp[i] |= dp[i - song]

    min_difference = float('inf')
    for i in range(half_duration, -1, -1):
        if dp[i]:
            min_difference = total_duration - 2 * i
            break

    return min_difference
