def min_concert_difference(a, b, c):
    total_duration = a + b + c

    half_duration = total_duration // 2

    if half_duration <= a:
        return abs(half_duration - a)
    elif half_duration <= a + b:
        return abs(half_duration - (a + b))
    else:
        return abs(half_duration - total_duration)

