def winner_of_game(nums):
    n = len(nums)
    dp = [[False] * n for _ in range(n)]

    # Each player will win if the subarray has only one element
    for i in range(n):
        dp[i][i] = True

    # Fill the table in bottom-up manner
    # For each subarray of length 2 to n
    for length in range(2, n+1):
        for i in range(n - length + 1):
            j = i + length - 1

            # If both players can win by choosing the left element
            if nums[i] < nums[i + 1] and not dp[i + 2][j]:
                dp[i][j] = True
            # If both players can win by choosing the right element
            elif nums[j] > nums[j - 1] and not dp[i][j - 2]:
                dp[i][j] = True
            # If player 1 can win by choosing the left element
            elif nums[i] < nums[j] and not dp[i + 1][j - 1]:
                dp[i][j] = True
            # If player 1 can win by choosing the right element
            elif nums[j] > nums[i] and not dp[i + 1][j - 1]:
                dp[i][j] = True

    return 'Alice' if dp[0][n - 1] else 'Bob'
