def winnerGame(A):
    n = len(A)
    dp = [[0] * n for _ in range(n)]

    for i in range(n):
        dp[i][i] = 1

    for l in range(2, n + 1):
        for i in range(n - l + 1):
            j = i + l - 1
            if (j - i + 1) % 2 == 1:
                a = dp[i + 1][j]
                b = dp[i][j - 1]
                if A[i] < A[j]:
                    dp[i][j] = max(a, b)
                else:
                    dp[i][j] = max(a, b) + 1
            else:
                a = dp[i + 1][j]
                b = dp[i][j - 1]
                dp[i][j] = max(a, b)

    if dp[0][n - 1] % 2 == 0:
        return "Bob"
    else:
        return "Alice"
