def winner_of_game(A):
    n = len(A)
    dp = [[0] * n for _ in range(n)]
    for i in range(n):
        dp[i][i] = A[i]

    for gap in range(1, n):
        for i in range(n - gap):
            j = i + gap
            dp[i][j] = max(A[i] - dp[i + 1][j], A[j] - dp[i][j - 1])

    if dp[0][n - 1] > 0:
        return 'Alice'
    else:
        return 'Bob'
