def find_smallest_d(arr):
    for d in range(1, max(arr)+1):
        colors = []
        for num in arr:
            if num % d == 0:
                colors.append("red")
            else:
                colors.append("blue")
        if all(colors[i] != colors[i+1] for i in range(len(colors)-1)):
            return d
    return 0
