def is_beautiful_coloring(arr):
    for d in range(1, max(arr) + 1):
        prev_color = "red" if arr[0] % d == 0 else "blue"
        for i in range(1, len(arr)):
            curr_color = "red" if arr[i] % d == 0 else "blue"
            if curr_color == prev_color:
                break
            prev_color = curr_color
        else:
            return d
    return 0

# Testing the function
print(is_beautiful_coloring([1, 2, 3, 4, 5])) # 4
print(is_beautiful_coloring([10, 5, 15])) # 0
print(is_beautiful_coloring([100, 10, 200])) # 0
