def find_smallest_d(arr):
    for d in range(1, min(arr) + 1):
        colors = []
        prev_color = None

        for num in arr:
            color = "red" if num % d == 0 else "blue"

            if prev_color == color:
                break

            prev_color = color
            colors.append(color)

        if len(colors) == len(arr):
            return d

    return 0
