def is_valid_date(date_string):
    try:
        month, day, year = date_string.split('-')
        month = int(month)
        day = int(day)
        year = int(year)
        if month < 1 or month > 12:
            return False
        if day < 1 or day > 31:
            return False
        if month == 2:
            if day > 29:
                return False
            if day == 29 and (year % 4 != 0 or (year % 100 == 0 and year % 400 != 0)):
                return False
        elif month in [4, 6, 9, 11]:
            if day > 30:
                return False
        return True
    except:
        return False
