def find_intersection(interval1, interval2):
    a1, a2 = map(int, interval1.strip('()').split(','))
    b1, b2 = map(int, interval2.strip('()').split(','))

    start = max(a1, b1)
    end = min(a2, b2)

    if start > end:
        return None
    elif start == end:
        return start
    else:
        return (start, end)
