def find_intersection(interval1, interval2):
    start1, end1 = interval1
    start2, end2 = interval2

    start = max(start1, start2)
    end = min(end1, end2)

    if start <= end:
        if start < end:
            return start, end
        else:
            return start
    else:
        return None

