def calculate_expression(integers, operations):
    expression = ""
    for i in range(len(integers) - 1):
        expression += str(integers[i]) + operations[i]
    expression += str(integers[-1])
    return eval(expression)

print(calculate_expression([2, 3, 4, 5], ['+', '*', '-']))
print(calculate_expression([2, 3, 4, 5], ['**', '*', '+']))
