def encrypt_string(string, shift):
    encrypted_string = ""
    for char in string:
        if char.isalpha():
            if char.islower():
                encrypted_string += chr((ord(char) - ord('a') + shift) % 26 + ord('a'))
            else:
                encrypted_string += chr((ord(char) - ord('A') + shift) % 26 + ord('A'))
        else:
            encrypted_string += char
    return encrypted_string
