def caesar_cipher(string, shift):
    encrypted_string = ""

    for char in string:
        if char.isalpha():
            if char.isupper():
                encrypted_char = chr((ord(char) - 65 + shift) % 26 + 65)
            else:
                encrypted_char = chr((ord(char) - 97 + shift) % 26 + 97)
        else:
            encrypted_char = char

        encrypted_string += encrypted_char

    return encrypted_string
