import datetime
import time

def parse_iso8601(date_str, delimiter='T', timezone=None):
    try:
        if timezone:
            date_format = "%Y-%m-%dT%H:%M:%S" + delimiter + "%z"
        else:
            date_format = "%Y-%m-%dT%H:%M:%S" + delimiter + "Z"
        dt = datetime.datetime.strptime(date_str, date_format)
        utctime = dt.replace(tzinfo=datetime.timezone.utc)
        timestamp = utctime.timestamp()
        return str(int(timestamp))
    except ValueError:
        return None
