from typing import List

def sort_books(n: int, m: int, books: List[str]) -> List[int]:
    indices = list(range(1, n+1))

    def compare(a: str, b: str) -> int:
        for i in range(m):
            if i % 2 == 0:
                if a[i] > b[i]:
                    return 1
                elif a[i] < b[i]:
                    return -1
            else:
                if a[i] < b[i]:
                    return 1
                elif a[i] > b[i]:
                    return -1
        return 0

    indices.sort(key=lambda i: books[i-1])
    indices.sort(key=lambda i: books[i-1][::2])

    return indices
