def minimal_difference(a, b, c):
    total_duration = a + 2*b + 3*c

    half_duration = total_duration // 2
    dp = [float('inf')] * (half_duration + 1)

    dp[0] = 0

    for i in range(a+1):
        for j in range(b+1):
            for k in range(c+1):
                duration = i + 2*j + 3*k
                if duration <= half_duration:
                    dp[duration] = min(dp[duration], half_duration - duration)

    return dp[half_duration]
