def song_concerts(a, b, c):
    total_duration = a + 2*b + 3*c
    half_duration = total_duration // 2

    song_combinations = []
    for i in range(a+1):
        for j in range(b+1):
            for k in range(c+1):
                duration = i + 2*j + 3*k
                if duration <= half_duration:
                    song_combinations.append(duration)

    min_difference = total_duration
    for duration in song_combinations:
        difference = abs(total_duration - 2*duration)
        if difference < min_difference:
            min_difference = difference

    return min_difference
