def min_difference(a, b, c):
    total_duration = a + 2 * b + 3 * c
    target_duration = total_duration // 2

    dp = [0] * (target_duration + 1)
    for i in range(a + 1):
        for j in range(target_duration, i - 1, -1):
            dp[j] = max(dp[j], dp[j - i] + i)

    for i in range(b + 1):
        for j in range(target_duration, i - 1, -1):
            dp[j] = max(dp[j], dp[j - 2 * i] + 2 * i)

    for i in range(c + 1):
        for j in range(target_duration, i - 1, -1):
            dp[j] = max(dp[j], dp[j - 3 * i] + 3 * i)

    return total_duration - 2 * dp[target_duration]
