def find_min_difference(a, b, c):
    total_duration = a + 2*b + 3*c
    half_duration = total_duration // 2

    dp = [[float('inf')] * (half_duration + 1) for _ in range(4)]
    dp[0][0] = 0

    for i in range(1, 4):
        for j in range(0, half_duration + 1):
            if j - i*min(a, b, c) >= 0:
                dp[i][j] = min(dp[i-1][j], dp[i][j-i*min(a,b,c)] + 1)
            else:
                dp[i][j] = dp[i-1][j]

    min_difference = total_duration - 2*dp[-1][-1]
    return min_difference
