from itertools import combinations

def minimal_difference(a, b, c):
    possible_durations = []
    for i in range(a+1):
        for j in range(b+1):
            for k in range(c+1):
                duration = i + 2*j + 3*k
                possible_durations.append(duration)
    possible_durations.sort()

    min_difference = possible_durations[-1] - possible_durations[0]
    for i in range(len(possible_durations) - 1):
        difference = possible_durations[i+1] - possible_durations[i]
        min_difference = min(min_difference, difference)

    return min_difference
