def winner_of_game(A):
    n = len(A)
    dp = [[0] * n for _ in range(n)]
    for d in range(n):
        for i in range(n - d):
            j = i + d
            if i == j:
                dp[i][j] = 1
            elif i + 1 == j:
                dp[i][j] = 1 if A[i] < A[j] else 2
            else:
                if A[i] < A[i + 1]:
                    dp[i][j] = 1 if dp[i + 1][j] == 2 else 2
                elif A[i] < A[j]:
                    dp[i][j] = 1 if dp[i + 1][j] == 2 or dp[i][j - 1] == 2 else 2
                else:
                    dp[i][j] = 1 if dp[i][j - 1] == 2 else 2
    return "Alice" if dp[0][n - 1] == 1 else "Bob"
