def find_winner(nums):
    n = len(nums)

    # create a matrix to store the winner for all possible sub-arrays
    dp = [[None] * n for _ in range(n)]

    # fill the matrix for all single-element sub-arrays
    for i in range(n):
        dp[i][i] = 'Alice'

    # fill the matrix for 2-element sub-arrays
    for i in range(n-1):
        if nums[i] < nums[i+1]:
            dp[i][i+1] = 'Alice'
        else:
            dp[i][i+1] = 'Bob'

    # fill the matrix for sub-arrays of length >= 3
    for length in range(3, n+1):
        for i in range(n-length+1):
            j = i + length - 1
            if nums[i] < nums[j] and dp[i+1][j] == 'Bob' and dp[i][j-1] == 'Bob':
                dp[i][j] = 'Alice'
            else:
                dp[i][j] = 'Bob'

    return dp[0][n-1]
