def smallest_beautiful_coloring(array):
    def is_beautiful(d):
        for i in range(1, len(array)):
            if array[i] % d == 0 and array[i-1] % d == 0:
                return False
            if array[i] % d != 0 and array[i-1] % d != 0:
                return False
        return True

    smallest_d = float('inf')
    for num in array:
        if num > 1:
            smallest_d = min(smallest_d, num)

    for d in range(2, smallest_d+1):
        if is_beautiful(d):
            return d

    return 0
