def find_smallest_divisor(arr):
    n = len(arr)

    # Check if a valid beautiful coloring is possible
    valid = True
    for i in range(1, n):
        if arr[i] == arr[i-1]:
            valid = False
            break

    # If a valid beautiful coloring is not possible, return -1
    if not valid:
        return -1

    # Find the smallest divisor that yields a beautiful coloring
    for d in range(2, min(arr)+1):
        is_beautiful = True
        for i in range(1, n):
            if arr[i] % d == 0 and arr[i-1] % d == 0:
                is_beautiful = False
                break
        if is_beautiful:
            return d

    # If no such divisor exists, return 0
    return 0
