def find_xth_lexicographically_smallest(k, x, s):
    def generate_ba_strings(s, i, k, current_string, ba_strings):
        if i == len(s):
            ba_strings.append(current_string)
        elif s[i] == '*':
            for j in range(k+1):
                generate_ba_strings(s, i+1, k, current_string + 'b'*j, ba_strings)
        else:
            generate_ba_strings(s, i+1, k, current_string + s[i], ba_strings)

    ba_strings = []
    generate_ba_strings(s, 0, k, '', ba_strings)
    ba_strings.sort()

    return ba_strings[x-1]
