def is_valid_date(date):
    try:
        # Split the date string by "-"
        month, day, year = date.split("-")

        # Convert month, day, and year to integers
        month = int(month)
        day = int(day)
        year = int(year)

        # Check if month is between 1 and 12
        if month < 1 or month > 12:
            return False

        # Check if day is valid for the corresponding month
        if month == 2:  # February
            if year % 4 == 0 and (year % 100 != 0 or year % 400 == 0):
                if day < 1 or day > 29:
                    return False
            else:
                if day < 1 or day > 28:
                    return False
        elif month in [4, 6, 9, 11]:  # Months with 30 days
            if day < 1 or day > 30:
                return False
        else:  # Months with 31 days
            if day < 1 or day > 31:
                return False

        # Check if the year is between 1000 and 2999
        if year < 1000 or year > 2999:
            return False

        return True
    except:
        return False
