def evaluate_expression(nums, operations):
    result = nums[0]
    for i in range(1, len(nums)):
        if operations[i-1] == '+':
            result += nums[i]
        elif operations[i-1] == '-':
            result -= nums[i]
        elif operations[i-1] == '*':
            result *= nums[i]
        elif operations[i-1] == '/':
            result /= nums[i]
        elif operations[i-1] == '**':
            result **= nums[i]
    return result
