def evaluate_expression(numbers, operations):
    result = numbers[0]
    for i in range(len(operations)):
        operation = operations[i]
        if operation == '+':
            result += numbers[i + 1]
        elif operation == '-':
            result -= numbers[i + 1]
        elif operation == '*':
            result *= numbers[i + 1]
        elif operation == '/':
            result /= numbers[i + 1]
        elif operation == '**':
            result **= numbers[i + 1]
    return result
