import ast

def apply_operations(integers, operations):
    expression = str(integers).replace(" ", "")
    for op in operations:
        expression = expression.replace(op, op[0])

    return eval(expression)

integers = ast.literal_eval("[2, 3, 4, 5]")
operations = ast.literal_eval("['+', '*', '-']")
apply_operations(integers, operations)
