def evaluate_expression(numbers, operations):
    expression = ""

    for i in range(len(numbers)-1):
        expression += str(numbers[i]) + operations[i]

    expression += str(numbers[-1])

    return eval(expression)

print(evaluate_expression([2, 3, 4, 5], ['+', '*', '-']))
print(evaluate_expression([2, 3, 4, 5], ['**', '*', '+']))
