def deepest_level_of_nesting(parentheses_string):
    max_level = 0
    current_level = 0

    for char in parentheses_string:
        if char == "(":
            current_level += 1
            if current_level > max_level:
                max_level = current_level
        elif char == ")":
            current_level -= 1

    return max_level
