def caesar_cipher_encrypt(string, shift):
    encrypted_string = ''
    for char in string:
        if char.isalpha():
            if char.islower():
                encrypted_char = chr((ord(char) - ord('a') + shift) % 26 + ord('a'))
            else:
                encrypted_char = chr((ord(char) - ord('A') + shift) % 26 + ord('A'))
        else:
            encrypted_char = char
        encrypted_string += encrypted_char
    return encrypted_string
