def sort_books(n, m, books):
    def compare(a, b, index):
        if index % 2 == 0:
            return a > b
        else:
            return a < b

    def merge_sort(arr, start, end, index):
        if start < end:
            mid = (start + end) // 2
            merge_sort(arr, start, mid, index)
            merge_sort(arr, mid + 1, end, index)
            merge(arr, start, mid, end, index)

    def merge(arr, start, mid, end, index):
        left = arr[start:mid + 1]
        right = arr[mid + 1:end + 1]
        left_index = 0
        right_index = 0
        arr_index = start
        while left_index < len(left) and right_index < len(right):
            if compare(left[left_index][index], right[right_index][index], index):
                arr[arr_index] = left[left_index]
                left_index += 1
            else:
                arr[arr_index] = right[right_index]
                right_index += 1
            arr_index += 1
        while left_index < len(left):
            arr[arr_index] = left[left_index]
            left_index += 1
            arr_index += 1
        while right_index < len(right):
            arr[arr_index] = right[right_index]
            right_index += 1
            arr_index += 1

    sorted_books = list(enumerate(books))
    merge_sort(sorted_books, 0, len(sorted_books) - 1, 0)
    merge_sort(sorted_books, 0, len(sorted_books) - 1, 1)

    return [idx + 1 for idx, _ in sorted_books]
