def asc_desc_ending_sort(n, m, lst):
    indexes = list(range(1, n+1))
    lst = sorted(list(zip(lst, indexes))) # Sort lexicographically ascending based on first letter
    for i in range(m):
        lst = sorted(lst, key=lambda x: x[0][i], reverse=(i % 2 == 0)) # Sort based on even or odd positions
    return [x[1] for x in lst]
