def min_changes(n, m, a, b):
    k_range = n - m + 2
    result = []

    for k in range(k_range):
        if k == 0:
            count = a.count(b)
            if count != 0:
                result.append(-1)
            else:
                result.append(a.count('0') + a.count('1'))

        elif k == 1:
            count = a.count(b)
            if count != 1:
                result.append(-1)
            else:
                result.append(a.count('0') + a.count('1'))

        else:
            count = a.count(b)
            if count != k:
                result.append(-1)
            else:
                result.append(a.count('0') + a.count('1'))

    return result

