def count_permutations(n, m, k, p):
    dp = [[[0] * (n+1) for _ in range(m+1)] for _ in range(n+1)]
    dp[0][0][0] = 1

    for i in range(1, n+1):
        for j in range(min(i, m)+1):
            for l in range(j+1):
                for last in range(i-l, i):
                    dp[i][j][l] = (dp[i][j][l] + dp[i-l-1][j-l][l or last > i-l]) % p

    ans = 0
    for j in range(k, m+1):
        for l in range(n-k+1):
            ans = (ans + dp[n][j][l]) % p

    return ans % p
