def count_permutations(n, m, k, p):
    def calculate_maxima(a):
        max_values = []
        for i in range(len(a)):
            max_values.append(max(a[:i+1]))
        return max_values

    def count_good_numbers(n, m, k):
        if m < k or m > k + n - 1:
            return 0
        dp = [[0] * (k + 1) for _ in range(n + 1)]
        mod = pow(10, 9) + 7
        dp[0][0] = 1
        for i in range(1, n + 1):
            for j in range(k + 1):
                dp[i][j] = (dp[i][j] + dp[i - 1][j]) % mod
                if j > 0:
                    dp[i][j] = (dp[i][j] + dp[i - 1][j - 1] * i) % mod
        return dp[n][k]
