def minimum_colors(n, k):
    edges = []
    for a in range(1, n):
        for b in range(a+1, n+1):
            edges.append((a, b))

    paths = []
    for i in range(len(edges)-k+1):
        for j in range(i+k, len(edges)+1):
            path = edges[i:j]
            paths.append(path)

    colors = set()
    for i in range(len(paths)):
        for j in range(i+1, len(paths)):
            common_edges = set(paths[i]).intersection(paths[j])
            colors.update(common_edges)

    return len(colors)
