def winner_game(A):
    sequence = []
    alice_turn = True
    while len(A) > 0:
        max_num = max(A[0], A[-1])
        if len(sequence) == 0 or max_num > sequence[-1]:
            if alice_turn:
                sequence.append(max_num)
            else:
                sequence.insert(0, max_num)
            A.remove(max_num)
            alice_turn = not alice_turn
        else:
            break
    if alice_turn:
        return 'Alice'
    else:
        return 'Bob'
