def determine_winner(A):
    sequence = []  # initialize the sequence
    turn = 0  # initialize the turn counter

    while len(A) > 0:
        if A[0] > A[-1]:
            num = A.pop(0)  # remove the leftmost number
        else:
            num = A.pop()  # remove the rightmost number

        if len(sequence) == 0 or num > sequence[-1]:
            sequence.append(num)  # append the number to the sequence
        else:
            break  # the game is already over

        turn += 1  # increment the turn counter

    if turn % 2 == 0:
        return 'Alice'  # Alice made the last move
    else:
        return 'Bob'  # Bob made the last move
