def find_beautiful_coloring(arr):
    min_d = float('inf')
    for i in range(len(arr)):
        for j in range(i+1, len(arr)):
            if arr[j] % arr[i] == 0 or arr[i] % arr[j] == 0:
                min_d = min(min_d, max(arr[i], arr[j]))
    return min_d if min_d != float('inf') else 0
