def beautiful_coloring(arr):
    divisors = set()
    for num in arr:
        for i in range(2, num+1):
            if num % i == 0:
                divisors.add(i)
    for divisor in divisors:
        for i in range(len(arr)-1):
            if (arr[i] % divisor == 0 and arr[i+1] % divisor == 0) or (arr[i] % divisor != 0 and arr[i+1] % divisor != 0):
                break
        else:
            return divisor
    return 0
