import itertools

def get_ba_string(k, x, s):
    possible_strings = []
    for replacements in itertools.product(range(k+1), repeat=s.count('*')):
        new_string = ''
        i = 0
        for char in s:
            if char == '*':
                new_string += 'b' * replacements[i]
                i += 1
            else:
                new_string += char
        possible_strings.append(new_string)
    possible_strings.sort()
    return possible_strings[x-1]
