from functools import reduce
from operator import add, sub, mul

def build_expression(int_list, op_list):
    op_dict = {
        '+': add,
        '-': sub,
        '*': mul
    }
    expression = ''
    for i, num in enumerate(int_list):
        expression += str(num)
        if i < len(op_list):
            expression += op_list[i]
    return reduce(lambda x, y: op_dict[y[1]](x, int(y[0])), zip(int_list[1:], op_list), int_list[0])
