import datetime

def parse_iso8601(date_str, delimiter='T', timezone=None):
    try:
        # Step 1: Sanitize the input datetime
        dt_str = date_str.strip(delimiter)
        if 'Z' in dt_str:
            dt_str = dt_str.replace('Z', '+0000')
        elif '+' not in dt_str:
            dt_str += '+0000'

        # Step 2: Convert the datetime into a UNIX timestamp
        dt = datetime.datetime.strptime(dt_str, "%Y-%m-%dT%H:%M:%S%z")
        timestamp = dt.timestamp()

        # Step 3: Convert the timestamp into a string
        return str(int(timestamp))
    except ValueError:
        return None
