def asc_desc_ending(n, m, book_list):
    def compare(a, b):
        for i in range(m):
            if a[i] != b[i]:
                if i % 2 == 0:
                    return ord(b[i]) - ord(a[i])
                else:
                    return ord(a[i]) - ord(b[i])
        return 0

    book_with_index = [(book, i+1) for i, book in enumerate(book_list)]
    book_with_index.sort(key=lambda x: x[0], cmp=compare)
    return [index for book, index in book_with_index]
