def asc_desc_ending_sort(n, m, books):
    # Initializing the list for mapping indices
    indices_map = list(range(1, n + 1))
    # Creating a function to sort characters by odd and even positions
    def sort_characters(book):
        return ''.join([char if i % 2 == 0 else chr(219 - ord(char)) for i, char in enumerate(book)])
    # Sorting the list using the built-in sort method
    sorted_indices_map = sorted(indices_map, key=lambda x: sort_characters(books[x-1]))
    return sorted_indices_map
