def min_difference(a, b, c):
    total = a + 2*b + 3*c
    mins = [False]*(total//2 + 1)
    mins[0] = True
    for i in range(a):
        for j in range(total//2, -1, -1):
            if mins[j]:
                mins[j+1] = True
    for i in range(b):
        for j in range(total//2, -1, -1):
            if mins[j]:
                mins[j+2] = True
    for i in range(c):
        for j in range(total//2, -1, -1):
            if mins[j]:
                mins[j+3] = True
    for i in range(total//2, -1, -1):
        if mins[i]:
            return abs(total - i - i)
    return -1
